import React, { useState, useEffect } from 'react';
import { Image, Typography, Button, Row, Col, Space, Tabs } from 'antd';
import {
  ShareAltOutlined,
  GlobalOutlined,
  FacebookOutlined,
  InstagramOutlined,
  TwitterOutlined,
} from '@ant-design/icons';
import MobileDetect from 'mobile-detect';
import styles from './style.module.scss';
import moment from 'moment';
import apis from 'apis';
import Sessions from '../../components/Sessions';
import Loader from '../../components/Loader';
const demoPic =
  'https://images.unsplash.com/photo-1548182880-8b7b2af2caa2?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80';

const ProfilePreview = () => {
  const md = new MobileDetect(window.navigator.userAgent);
  const isMobileDevice = Boolean(md.mobile());
  const [coverImage, setCoverImage] = useState(null);
  const [profileImage, setProfileImage] = useState(null);
  const [selectedTab, setSelectedTab] = useState(0);
  const [upcomingSession] = useState([
    {
      id: 1,
      sessionImage: demoPic,
      sessionType: 'Group Session',
      name: 'Introduction to Geometry and Trigonometry',
      date: moment().format(),
      starttime: moment().format(),
      endtime: moment().add(2, 'hours'),
    },
    {
      id: 2,
      sessionImage: demoPic,
      sessionType: 'Group Session',
      name: 'Introduction to Geometry and Trigonometry',
      date: moment().format(),
      starttime: moment().format(),
      endtime: moment().add(2, 'hours'),
    },
    {
      id: 3,
      sessionImage: demoPic,
      sessionType: 'Group Session',
      name: 'Introduction to Geometry and Trigonometry',
      date: moment().format(),
      starttime: moment().format(),
      endtime: moment().add(2, 'hours'),
    },
    {
      id: 4,
      sessionImage: demoPic,
      sessionType: 'Group Session',
      name: 'Introduction to Geometry and Trigonometry',
      date: moment().format(),
      starttime: moment().format(),
      endtime: moment().add(2, 'hours'),
    },
  ]);
  const [isLoading, setIsLoading] = useState(true);
  const [profile, setProfile] = useState(null);

  const getProfileDetails = async () => {
    try {
      const { data } = await apis.user.getProfile();
      if (data) {
        setProfile(data);
        setCoverImage(data.profile.cover_image_url);
        setProfileImage(data.profile.profile_image_url);
        setIsLoading(false);
      }
    } catch (error) {
      console.log(error);
      setIsLoading(false);
    }
  };
  useEffect(() => {
    getProfileDetails();
  }, []);
  const handleChangeTab = (key) => {
    setSelectedTab(key);
  };
  return (
    <Loader loading={isLoading} size="large" text="Loading profile">
      {/* ======INTRO========= */}
      <div className={styles.imageWrapper}>
        <div
          style={{
            backgroundColor: '#f5f5f5',
            textAlign: 'center',
            borderRadius: 8,
            margin: -20,
          }}
        >
          <Image
            width={coverImage ? '100%' : 200}
            height={300}
            className={styles.coverImage}
            src={coverImage ? coverImage : 'error'}
            fallback="data:image/png;base64,"
          />
        </div>

        <Image
          className={isMobileDevice ? styles.profileImageSmall : styles.profileImage}
          width={isMobileDevice ? 80 : 120}
          height={isMobileDevice ? 80 : 120}
          src={profileImage ? profileImage : 'error'}
          fallback="data:image/png;base64,"
        />
      </div>
      <Row justify="space-between" align="middle">
        <Col xs={8} md={24}></Col>
        <Col xs={10} md={{ span: 6, offset: 6 }}>
          <Typography.Title level={isMobileDevice ? 4 : 2}>{profile?.full_name}</Typography.Title>
        </Col>
        <Col xs={6} md={{ span: 6, offset: 6 }}>
          <Button size={isMobileDevice ? 'small' : 'middle'} icon={<ShareAltOutlined />}>
            Share
          </Button>
        </Col>
        <Col xs={24} md={{ span: 18, offset: 3 }}>
          <Typography.Text type="secondary">{profile?.profile?.description}</Typography.Text>
        </Col>
        <Col xs={24} md={{ span: 18, offset: 3 }}>
          <Space size={'middle'}>
            <a
              href="/#"
              onClick={() => window.open(profile?.profile?.social_media_links?.website)}
              target="_blank"
              rel="noopener noreferrer"
            >
              <GlobalOutlined className={styles.socialIcon} />
            </a>
            <a
              href="/#"
              onClick={() => window.open(profile?.profile?.social_media_links?.facebook_link)}
              target="_blank"
              rel="noopener noreferrer"
            >
              <FacebookOutlined className={styles.socialIcon} />
            </a>
            <a
              href="/#"
              onClick={() => window.open(profile?.profile?.social_media_links?.twitter_link)}
              target="_blank"
              rel="noopener noreferrer"
            >
              <TwitterOutlined className={styles.socialIcon} />
            </a>
            <a
              href="/#"
              onClick={() => window.open(profile?.profile?.social_media_links?.instagram_link)}
              target="_blank"
              rel="noopener noreferrer"
            >
              <InstagramOutlined className={styles.socialIcon} />
            </a>
          </Space>
        </Col>
      </Row>

      {/* =====SESSION======== */}
      <Row className={styles.mt50}>
        <Col span={24}>
          <Typography.Title level={isMobileDevice ? 4 : 2}>Sessions</Typography.Title>
        </Col>
        <Col span={24}>
          <Tabs defaultActiveKey={selectedTab} onChange={handleChangeTab}>
            <Tabs.TabPane tab="Upcoming Sessions" key="0">
              <Sessions sessions={upcomingSession} />
            </Tabs.TabPane>
            <Tabs.TabPane tab="Past Sesions" key="1">
              <Sessions sessions={upcomingSession} />
            </Tabs.TabPane>
          </Tabs>
        </Col>
      </Row>

      {/* =====TESTIMONIALS======== */}
      <Row className={styles.mt50}>
        <Col span={24}>
          <Typography.Title level={isMobileDevice ? 4 : 2}>What attendees are saying</Typography.Title>
        </Col>
        <Col span={24}></Col>
      </Row>
    </Loader>
  );
};
export default ProfilePreview;
